import React, { Component } from 'react'
import Paper from '@material-ui/core/Paper'
const styles = {
  container: {
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    margin: 16,
    padding: 16,
    backgroundColor: '#f2f0f0'
  },
  text: {
    marginRight: 15,
    maxWidth: 500
  },
  title: {
    fontSize: '50px',
    color: '#5a5a5a',
    fontWeight: 400,
    lineHeight: 1,
    marginBottom: 15
  },
  date: {
    fontSize: '38px',
    color: '#6c757d',
    fontWeight: 400,
    lineHeight: 1,
    letterSpacing: '-0.05rem',
    marginTop: 15,
    marginBottom: 20
  },
  description: {
    fontSize: '1.25rem',
    color: '#5a5a5a',
    fontWeight: 300,
    lineHeight: 1.5,
    marginTop: 10
  },
  picture: {
    marginLeft: 15
  }
}

export default class Event extends Component {
  renderText() {
    return (
      <div style={styles.text}>
        <div style={styles.title}>{this.props.title}</div>
        <div style={styles.date}>{this.props.date}</div>
        <div style={styles.description}>{this.props.contents}</div>
      </div>
    )
  }

  renderPicture() {
    return (
      <div style={styles.picture}>
        <img
          class="featurette-image img-fluid mx-auto"
          src={this.props.image}
          alt="Graduation Image"
        />
      </div>
    )
  }

  render() {
    if (this.props.orientation === 'default') {
      return (
        <Paper style={styles.container}>
          {this.renderText()}
          {this.renderPicture()}
        </Paper>
      )
    } else if (this.props.orientation === 'flipped') {
      return (
        <Paper style={styles.container}>
          {this.renderPicture()}
          {this.renderText()}
        </Paper>
      )
    }
  }
}
