import React, { Component } from 'react'
const styles = {
  container: {
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'center',
    margin: 16
  },
  text: {
    order: 1,
    flexGrow: 3,
    marginRight: 15
  },
  title: {
    fontSize: '50px',
    color: '#5a5a5a',
    fontWeight: 400,
    lineHeight: 1,
    marginBottom: 15
  },
  date: {
    fontSize: '38px',
    color: '#6c757d',
    fontWeight: 400,
    lineHeight: 1,
    letterSpacing: '-0.05rem',
    marginTop: 15,
    marginBottom: 20
  },
  description: {
    fontSize: '1.25rem',
    color: '#5a5a5a',
    fontWeight: 300,
    lineHeight: 1.5,
    marginTop: 10
  },
  picture: {
    order: 2,
    flexGrow: 1,
    marginLeft: 15
  }
}

export default class Event extends Component {
  render() {
    if (this.props.orientation == 'default') {
      return (
        <div style={styles.container}>
          <div style={styles.text}>
            <div style={styles.title}>{this.props.title}</div>
            <div style={styles.date}>{this.props.date}</div>
            <div style={styles.description}>{this.props.contents}</div>
          </div>
          <div style={styles.picture}>
            <img
              class="featurette-image img-fluid mx-auto"
              src={this.props.image}
              alt="Graduation Image"
            />
          </div>
        </div>
      )
    } else if (this.props.orientation == 'flipped') {
      return (
        <div style={styles.container}>
          <div style={styles.picture}>
            <img
              class="featurette-image img-fluid mx-auto"
              src={this.props.image}
              alt="Graduation Image"
            />
          </div>
          <div style={styles.text}>
            <div style={styles.title}>{this.props.title}</div>
            <div style={styles.date}>{this.props.date}</div>
            <div style={styles.description}>{this.props.contents}</div>
          </div>
        </div>
      )
    }
  }
}
